// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title Configurable contract interface
 * @dev Interface for a contract that allows for dynamic configuration of uint256 and address values.
 */
interface IConfig {
  /**
   * @dev Returns a number by name
   * @param _name A number name
   * @return The value of the number
   */
  function getNumber(bytes32 _name) external view returns (uint256);

  /**
   * @dev Returns an address by name
   * @param _name An address name
   * @return The address value
   */
  function getAddress(bytes32 _name) external view returns (address);

  /**
   * @dev Returns a list of registered config variables names
   * @return Array of registered variables names
   */
  function variables() external view returns (bytes32[] memory);

  /**
   * @dev Returns a minDeposit value of the entity
   * @param _name An entity name
   * @return The value of the number
   */
  function getMinDeposit(bytes32 _name) external view returns (uint256);

  /**
   * @dev Changes variable uint256 of value
   * @param name Name of variable
   * @param value Value of variable
   */
  function config(bytes32 name, uint256 value) external;

  /**
   * @dev Changes variable address of value
   * @param name Name of variable
   * @param value Value of variable
   */
  function config(bytes32 name, address value) external;

  /**
   * @dev Sets minimum deposits values
   * @param _kinds Supported entity types
   * @param _minDeposits Minimum value of deposit
   */
  function setMinDeposits(
    bytes32[] memory _kinds,
    uint256[] memory _minDeposits
  ) external;
}

contracts/libraries/SignatureUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

library SignatureUtils {
  error InvalidSignature();

  /// @dev Splits signature into v/r/s form
  function split(
    bytes memory signature
  ) internal pure returns (uint8 v, bytes32 r, bytes32 s) {
    if (signature.length == 65) {
      assembly {
        r := mload(add(signature, 0x20))
        s := mload(add(signature, 0x40))
        v := byte(0, mload(add(signature, 0x60)))

        if eq(v, 0) {
          v := 27
        }

        if eq(v, 1) {
          v := 28
        }
      }
    } else {
      revert InvalidSignature();
    }
  }
}

contracts/libraries/Utils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";

library Utils {
  using SafeMathUpgradeable for uint256;

  /// Constants

  bytes32 public constant PAYMENT_OPTION_TYPE_HASH =
    keccak256("PaymentOption(bytes32 id,uint256 price,address asset)");

  bytes32 public constant CANCEL_OPTION_TYPE_HASH =
    keccak256("CancelOption(uint256 time,uint256 penalty)");

  bytes32 public constant OFFER_TYPE_HASH =
    keccak256(
      // solhint-disable-next-line max-line-length
      "Offer(bytes32 id,uint256 expire,bytes32 supplierId,uint256 chainId,bytes32 requestHash,bytes32 optionsHash,bytes32 paymentHash,bytes32 cancelHash,bool transferable,uint256 checkIn,uint256 checkOut)"
    );

  bytes32 public constant CHECK_IN_TYPE_HASH =
    keccak256("Voucher(bytes32 id,address signer)");

  /// Errors

  /// @dev Thrown when percents value greater than 100
  error InvalidPercent();

  /// Data structures

  /**
   * @dev Payment option
   * @param id Unique ID of the payment option
   * @param price The price of the asset in wei
   * @param asset The address of the ERC20 token used for payment
   */
  struct PaymentOption {
    bytes32 id;
    uint256 price;
    address asset;
  }

  /**
   * @dev Deal cancellation option
   * @param time The number of seconds before checkIn
   * @param penalty The percentage of the total sum to be paid as a penalty if the deal is cancelled
   */
  struct CancelOption {
    uint256 time;
    uint256 penalty;
  }

  /**
   * @dev Offer payload
   * @param id The unique ID of the offer
   * @param expire The time when the offer expires (in seconds since the Unix epoch)
   * @param supplierId The unique ID of the supplier offering the deal
   * @param chainId The ID of the network chain where the deal is to be executed
   * @param requestHash The hash of the request made by the buyer
   * @param optionsHash The hash of the payment and cancellation options for the deal
   * @param paymentHash The hash of the payment option used for the deal
   * @param cancelHash The hash of the cancellation option used for the deal
   * @param transferable Indicates whether the deal NFT is transferable or not
   * @param checkIn The check-in time for the deal (in seconds since the Unix epoch)
   * @param checkOut The check-out time for the deal (in seconds since the Unix epoch)
   */
  struct Offer {
    bytes32 id;
    uint256 expire;
    bytes32 supplierId;
    uint256 chainId;
    bytes32 requestHash;
    bytes32 optionsHash;
    bytes32 paymentHash;
    bytes32 cancelHash;
    bool transferable;
    uint256 checkIn;
    uint256 checkOut;
  }

  /**
   * @dev Deal status
   */
  enum DealStatus {
    Created, // Just created
    Claimed, // Claimed by the supplier
    Rejected, // Rejected by the supplier
    Refunded, // Refunded by the supplier
    Cancelled, // Cancelled by the buyer
    CheckedIn, // Checked In
    CheckedOut, // Checked Out
    Disputed // Dispute started
  }

  /**
   * @dev Deal storage struct
   * @param offer Offer payload
   * @param price Deal price
   * @param asset Deal asset
   * @param status Current deal status
   */
  struct Deal {
    uint256 created;
    Offer offer;
    bytes32 retailerId;
    address buyer;
    uint256 price;
    address asset;
    DealStatus status;
  }

  /// @dev Create a has of bytes32 array
  function hash(bytes32[] memory _hashes) internal pure returns (bytes32) {
    return keccak256(abi.encodePacked(_hashes));
  }

  /// @dev Creates a hash of a PaymentOption
  function hash(
    PaymentOption memory _paymentOptions
  ) internal pure returns (bytes32) {
    return
      keccak256(
        abi.encodePacked(
          PAYMENT_OPTION_TYPE_HASH,
          _paymentOptions.id,
          _paymentOptions.price,
          _paymentOptions.asset
        )
      );
  }

  /// @dev Creates a hash of a CancelOption
  function hash(CancelOption memory _cancel) internal pure returns (bytes32) {
    return
      keccak256(
        abi.encodePacked(CANCEL_OPTION_TYPE_HASH, _cancel.time, _cancel.penalty)
      );
  }

  /// @dev Creates a hash of an array of PaymentOption
  function hash(
    PaymentOption[] memory _paymentOptions
  ) internal pure returns (bytes32) {
    bytes32[] memory hashes = new bytes32[](_paymentOptions.length);

    for (uint256 i = 0; i < _paymentOptions.length; i++) {
      hashes[i] = hash(_paymentOptions[i]);
    }

    return hash(hashes);
  }

  /// @dev Creates a hash of an array of CancelOption
  function hash(
    CancelOption[] memory _cancelOptions
  ) internal pure returns (bytes32) {
    bytes32[] memory hashes = new bytes32[](_cancelOptions.length);

    for (uint256 i = 0; i < _cancelOptions.length; i++) {
      hashes[i] = hash(_cancelOptions[i]);
    }

    return hash(hashes);
  }

  /// @dev Creates a hash of an Offer
  function hash(Offer memory _offer) internal pure returns (bytes32) {
    return
      keccak256(
        abi.encode(
          OFFER_TYPE_HASH,
          _offer.id,
          _offer.expire,
          _offer.supplierId,
          _offer.chainId,
          _offer.requestHash,
          _offer.optionsHash,
          _offer.paymentHash,
          _offer.cancelHash,
          _offer.transferable,
          _offer.checkIn,
          _offer.checkOut
        )
      );
  }

  /// @dev Create a hash of check-in data
  function hashCheckInOut(
    bytes32 _id,
    address _signer
  ) internal pure returns (bytes32) {
    return keccak256(abi.encode(CHECK_IN_TYPE_HASH, _id, _signer));
  }

  /// @dev Calculates percentage value
  function _percentage(
    uint256 value,
    uint256 percent
  ) internal pure returns (uint256) {
    if (percent > 100) {
      revert InvalidPercent();
    }
    return value.mul(1000).mul(percent).div(100).div(1000);
  }
}

contracts/Market.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./ERC721Token.sol";
import "./DealsRegistry.sol";
import "./libraries/Utils.sol";

/**
 * @title Market
 * @dev This contract enables the creation and management of deals
 * @custom:security-contact security@windingtree.com
 */
contract Market is ERC721Token, DealsRegistry {
  /// @dev Mapping of token Id on offer Id
  mapping(uint256 => bytes32) public tokenOffers;

  /// @dev Mapping of offer Id on token Id
  mapping(bytes32 => uint256) public offerTokens;

  /// Throws when NFT transfer is not allowed by offer rule
  error TokenTransferNotAllowed();

  /**
   * @dev Constructor of ERC721Token
   */
  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor() {
    _disableInitializers();
  }

  /**
   * @dev Initializes the Market contract with the given arguments
   * @param _owner The owner of the contract
   * @param _name The name of the contract
   * @param _version The version of the contract
   * @param _config The protocol config contract address
   * @param _entities Entities registry contract address
   */
  function initialize(
    address _owner,
    string memory _name,
    string memory _version,
    address _config,
    address _entities
  ) external initializer {
    _transferOwnership(_owner);

    // Initialize ERC721 token
    __ERC721Token_init("DealToken", "DEAL");

    // Initialize Deals registry
    __DealsRegistry_init(_name, _version, _config, _entities);
  }

  /// Getters

  /**
   * @dev Returns offerId linked to the token
   * @param tokenId The ID of the token
   * @return offerId The ID of the offer linked to the token
   */
  function resolveTokenId(
    uint256 tokenId
  ) external view returns (bytes32 offerId) {
    _requireMinted(tokenId);
    offerId = tokenOffers[tokenId];
  }

  /// Pausable features

  /**
   * @dev Pauses the contract
   */
  function pause() public onlyOwner {
    _pause();
  }

  /**
   * @dev Unpauses the contract
   */
  function unpause() public onlyOwner {
    _unpause();
  }

  /// Features

  /**
   * @dev Executes logic before a deal is created
   * @param offer The details of the offer
   * @param price The price of the offer
   * @param asset The address of the asset
   * @param signs The signatures of the offer
   */
  function _beforeCreate(
    Utils.Offer memory offer,
    uint256 price,
    address asset,
    bytes[] memory signs
  ) internal override(DealsRegistry) whenNotPaused {
    super._beforeCreate(offer, price, asset, signs);
  }

  /**
   * @dev Executes logic after a deal is created
   * @param offer The details of the offer
   * @param price The price of the offer
   * @param asset The address of the asset
   * @param signs The signatures of the offer
   */
  function _afterCreate(
    Utils.Offer memory offer,
    uint256 price,
    address asset,
    bytes[] memory signs
  ) internal override(DealsRegistry) {
    // After-deal logic
    super._afterCreate(offer, price, asset, signs);
  }

  /**
   * @dev Executes logic after a deal is claimed
   * @param offerId The ID of the offer
   * @param buyer The address of the buyer
   */
  function _afterClaim(
    bytes32 offerId,
    address buyer
  ) internal override(DealsRegistry) {
    // Minting of a token
    uint256 tokenId = safeMint(buyer);
    // Create a map of token Id on offer Id
    tokenOffers[tokenId] = offerId;
    // Create a map of offer Id on token Id
    offerTokens[offerId] = tokenId;
    super._afterClaim(offerId, buyer);
  }

  /**
   * @dev Executes logic after a deal is canceled
   * @param offerId The ID of the offer
   */
  function _afterCancel(bytes32 offerId) internal override(DealsRegistry) {
    uint256 tokenId = offerTokens[offerId];

    // If token has been minted we must burn it
    if (tokenId != 0) {
      safeBurn(tokenId);
      delete tokenOffers[tokenId];
      delete offerTokens[offerId];
    }

    super._beforeCancel(offerId);
  }

  /// ERC721 features

  /**
   * @dev Returns the token URI of the given token ID
   * @param tokenId The ID of the token
   * @return The token URI of the given token ID
   */
  function tokenURI(
    uint256 tokenId
  ) public view override(ERC721Upgradeable) returns (string memory) {
    _requireMinted(tokenId);
    // TODO: Generate data-uri that depends on the id
    return "";
  }

  /**
   * @dev Executes before a token transfer
   * @param from The address to transfer the token from
   * @param to The address to transfer the token to
   * @param tokenId The ID of the token being transferred
   * @param batchSize The size of the batch being transferred
   *
   * NOTE: Initially minted token is transferred to his owner without any restrictions
   * All other transfers are managed according the following requirements:
   *
   * - token must be linked to an offerId
   * - token can be transferred or not according to the configuration of offer
   * - token can not be transferred when the deal status is `Claimed` only
   */
  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 tokenId,
    uint256 batchSize
  ) internal override(ERC721Token) whenNotPaused {
    // Execute the logic when the function called not from `_mint` or `_burn`
    if (from != address(0) && to != address(0)) {
      bytes32 offerId = tokenOffers[tokenId];

      if (offerId == bytes32(0)) {
        revert DealNotFound();
      }

      Utils.Deal storage offerDeal = deals[offerId];

      // Prevent transfer of token when this is not allowed by the offer
      // or the deal is in the non-transferrable status
      if (
        !offerDeal.offer.transferable ||
        offerDeal.status != Utils.DealStatus.Claimed
      ) {
        revert TokenTransferNotAllowed();
      }

      // Change the deal buyer to the new token owner
      offerDeal.buyer = to;
    }

    super._beforeTokenTransfer(from, to, tokenId, batchSize);
  }

  uint256[50] private __gap;
}

